function pushbutton6_Callback(hObject, eventdata, handles)
global Gui time_limit

Lq=min5_statics(Gui,time_limit)
disp(Lq)
P=[];
for i=1:size(Lq,2)
P=[P;Lq(i).lq(2,:)];
end 
P=sum(P);
figure(4)
plot((5:5:time_limit),P)
grid on
set(gca,'Xtick',0:5:180)
set(gca,'FontSize', 7)
lgd1=legend("整个系统每五分钟排队人数之和统计",'Location','northwest');
title(lgd1,"图例");

function pushbutton7_Callback(hObject, eventdata, handles)
global Gui time_limit
Lq=min5_statics(Gui,time_limit);
figure(3)
for i=1:size(Lq,2)
    plot((5:5:time_limit),Lq(i).lq(2,:));
    grid on
    hold on
end 
set(gca,'Xtick',0:5:180)
set(gca, 'FontSize', 7)
lgd=num2str((1:size(Lq,2))');
lgd1=legend(lgd,'Location','northwest');
title(lgd1,"柜台编号");

